func createRecordMenu(menu model.Menu) (err error) {
	database := db.DBConn()
	defer database.Close()
	tx, err := db.SQLBegin(database)
	if err != nil {
		return err
	}
	Level := menu.Level
	Day := menu.Day
	//ClassID := menu.ClassID
	AssignedDate := menu.AssignedDate
	Note := menu.Note
	DateCreate := time.Now()
	DateUpdate := time.Now()
	insForm, err := db.SQLExec(tx, "INSERT INTO Menu_detail(menu_id, session_id, food_name, note, date_create,date_update, update_count) VALUES(?,?,?,?,?,?,?)")
	if err != nil {
		return err
	}
	if _, err := insForm.Exec(Day, Level, AssignedDate, Note, DateCreate, DateUpdate, 0); err != nil {
		tx.Rollback()
		return err
	}
	tx.Commit()
	return nil
}

