func getDataMenuFromDB(id string) []byte {
	database := db.DBConn()
	defer database.Close()
	var (
		data    model.Menu
		records []model.Menu
	)
	rows, err := database.Query("SELECT * FROM Menu_detail WHERE id= ?", id)
	if err != nil {
		fmt.Println(err)
		return nil
	}
	for rows.Next() {
		var date, datecreate time.Time
		var count int
		rows.Scan(&data.Id, &data.Day, &data.Level, &data.AssignedDate, &data.Note, &datecreate, &date, &count)
		records = append(records, data)
	}
	defer rows.Close()
	if records == nil {
		return nil
	}
	jsonResponse, jsonError := json.Marshal(records)
	if jsonError != nil {
		fmt.Println(jsonError)
		return nil
	}
	return jsonResponse
}

